import React, { useState, useEffect } from 'react';
//scripts to deploy google maps. 
import { GoogleMap, withScriptjs, withGoogleMap, Marker, InfoWindow } from 'react-google-maps';
import { requestParks, retrieveParks, retrieveParksError } from '../actions/parkActions';
import { useDispatch, useSelector } from 'react-redux';
//components
import ParkSidebar from '../ParkSidebar';


import styled from 'styled-components';




const Map = ({ coordinates }) => {
    //pass down coordinates as props.

    const dispatch = useDispatch();

    const allParks = useSelector(state => state.parkReducer)

    const [parkInfo, setParkInfo] = useState(null)

    //bool to control pakr clicking. 
    const [parkMenu, setParkMenu] = useState(false);


    const handleParks = () => {
        const handleNearestPlaces = async () => {
            dispatch(requestParks())

            try {
                let response = await fetch('/nearbySearch', {
                    method: "POST",
                    headers: {
                        'Accept': 'application/json',
                        'Content-type': 'application/json'
                    },
                    body: JSON.stringify(coordinates)
                })
                let nearbyParks = await response.json();
                if (response.status === 200) {
                    dispatch(retrieveParks(nearbyParks.results))
                }
                else {
                    dispatch(retrieveParksError())
                }
            }
            catch (err) {
                console.log(err)
            }
        }

        handleNearestPlaces();
    }

    const handleParkSelect = (park) => {
        setParkInfo(park)
        setParkMenu(true)
    }

    return (
        <>
            <ParkSidebar parkMenu={parkMenu} setParkMenu={setParkMenu}></ParkSidebar>
            <GoogleMap defaultZoom={15}
                center={{ lat: coordinates.lat, lng: coordinates.lng }}>
                <button onClick={handleParks}>FIND PARKS</button>
                {allParks.status === 'retrieved parks.' && allParks.parks.map(park => {
                    console.log(park.geometry.location)
                    return (
                        <Marker
                            style={{ backgroundColor: 'blue' }}
                            position={park.geometry.location} onClick={() => handleParkSelect(park)} />
                    )
                })}
                <Marker position={coordinates}></Marker>
                {parkInfo !== null &&
                    //control the tippy for each map point.
                    <InfoWindow
                        position={{
                            lat: parkInfo.geometry.location.lat,
                            lng: parkInfo.geometry.location.lng
                        }}
                        onCloseClick={() => setParkInfo(null)}>
                        <div>
                            <div>park details</div>
                            <div>{parkInfo.name}</div>
                        </div>
                    </InfoWindow>}
            </GoogleMap>
        </>

    )

}
//must wrap.
const WrappedMap = withScriptjs(withGoogleMap(Map));


export default WrappedMap;

const Sidebar = styled.div`
position: fixed;
top: 0;
left: 0;
width: 250px;
height: 70vh;

`
