import React, { useEffect } from 'react';
//scripts to deploy google maps. 
import { GoogleMap, withScriptjs, withGoogleMap, Marker } from 'react-google-maps';


const Map = ({ coordinates }) => {
    //pass down coordinates as props.



    useEffect(() => {

        //

        const handleNearestPlaces = async () => {

            let latitude = coordinates.lat
            let longitude = coordinates.lng

            let radMetter = 2 * 1000; // Search withing 2 KM radius

            const url = 'https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=' + latitude + ',' + longitude + '&radius=' + radMetter + '&key=' + 'AIzaSyAt-D4AMalUpyQjUe3laQYyjjNgy_hcCOc'

            let responseNearestPlaces = await fetch(url)







        }

        handleNearestPlaces();

    }, [])











    return (

        <GoogleMap defaultZoom={15}
            center={{ lat: coordinates.lat, lng: coordinates.lng }}>
            <Marker position={coordinates}></Marker>
        </GoogleMap>
    )

}
//must wrap.
const WrappedMap = withScriptjs(withGoogleMap(Map));


export default WrappedMap;
