import React, { useState } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import WrappedMap from '../Map/Map';

//
import GoogleAddress from '../GoogleAddress';




const Sports = () => {

    const userLoggedIn = useSelector(state => state.userReducer)

    const [mapBool, setMapBool] = useState(false);


    //handlers.

    const handleMapBool = (e) => {
        e.preventDefault();

    }



    return (
        <Wrapper>
            {userLoggedIn.status !== "authenticated" ?
                <h1>You must be logged in to find games.</h1> :


                <div>

                    {/* // */}
                    <h1>Welcome. Enter an address to begin.</h1>
                    <GoogleAddress></GoogleAddress>
                    {/* //call component to find games. */}
                    {/* <form onSubmit={handleMapBool}>
                        <input type='text' placeholder='enter address' onChange={(e) => setAddress(e.target.value)}></input>
                        <button type='submit'>Go</button>
                    </form> */}

                    {mapBool && <div style={{ width: '100vw', height: '80vh' }}>
                        <WrappedMap
                            googleMapURL={`https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places`}
                            loadingElement={<div style={{ height: '100%' }}></div>}
                            containerElement={<div style={{ height: '100%' }}></div>}
                            mapElement={<div style={{ height: '100%' }}></div>}
                        ></WrappedMap>
                    </div>}
                </div>

            }
        </Wrapper>

    )

}

export default Sports;

const Wrapper = styled.div`

h1 {
    text-align: center;
}

`
