import React, { useState, useEffect } from 'react';
import styled from 'styled-components'


const ParkSidebar = ({ parkInfo, parkMenu, setParkMenu }) => {

    console.log(parkInfo)

    const [image, setImage] = useState(null)


    //-----------------------SELECTED PARK PHOTO ------------------------------
    useEffect(() => {
        const handlePhoto = async () => {
            if (parkInfo.photos[0].photo_reference) {
                let photo = parkInfo.photos[0].photo_reference;
                console.log(photo, 'inside handle photo')
                try {
                    let response = await fetch('/parkPhoto', {
                        method: "POST",
                        headers: {
                            'Accept': 'application/json',
                            'Content-type': 'application/json'
                        },
                        body: JSON.stringify({ photo })
                    })
                    if (response.status === 200) {
                        let image = await response.json();
                        setImage(image.image)
                    }
                    else {
                        console.log('error occured fetching park image.')
                    }
                }
                catch (err) {
                    console.log("error occured in Catch for handlePhoto", err)
                }
            }
            else {
                console.log('photo does not exist')
            }
        }
        handlePhoto();
    }, [parkInfo])



    return (
        <>

            {parkInfo !== null &&
                <Wrapper>

                    <Title>
                        <h1>{parkInfo.name}</h1>
                        <img src={parkInfo.icon}></img>
                    </Title>

                    <h2>{parkInfo.formatted_address}</h2>
                    {image !== null && <img src={image}></img>}
                    <div>Ratings: {parkInfo.user_ratings_total}</div>
                </Wrapper>}
            {parkMenu && <Btn onClick={() => setParkMenu(false)}>X</Btn>}




        </>

    )

}


export default ParkSidebar;

const Btn = styled.button`
position: absolute;
top: 30vh;
left: 400px;
z-index: 101;
transition-duration: 5s;

`

const Wrapper = styled.div`

word-wrap: break-word;
padding-right: 4rem;
    padding-bottom: 20px; 
    margin: 2rem 0;
    width: 400px;
    text-align: center;
    font-size: 1rem;

    h2 {
        font-size: 1.1rem;
        padding: 1rem;
    }
`

const Title = styled.div`
margin-top: 2.5rem;
display: flex;
justify-content: center;
position: relative;

img {
    position: absolute;
    bottom: 50px;
}


`
