import React, { useState } from 'react';
import styled from 'styled-components'


const ParkSidebar = ({ parkInfo, parkMenu, setParkMenu }) => {

    console.log(parkInfo)



    return (
        <>{parkInfo &&
            <div>
                <Sidebar style={(parkMenu) ? { transform: "translateX(0px)" } : {
                    transform: "translateX(-400px)"
                }}>
                    <Wrapper>
                        {parkInfo.photos[0].html_attributions[0]}
                        <h1>{parkInfo.name}</h1>
                        <h2>{parkInfo.formatted_address}</h2>
                        <img src={parkInfo.icon}></img>
                    </Wrapper>





                </Sidebar>

                {parkMenu && <Btn onClick={() => setParkMenu(false)}>X</Btn>}
            </div>

        }

        </>

    )

}


export default ParkSidebar;

const Sidebar = styled.div`
   position: fixed; 
    left: 0; 
    width:400px;
    height: 70vh; 
    transition-duration: .7s;
    top: 30vh; 

    background-image: linear-gradient(-60deg, #16a085 0%, #f4d03f 100%);


    `
const Btn = styled.button`
position: absolute;
top: 30vh;
right: 400px;
z-index: 101;
transition-duration: 5s;

`

const Wrapper = styled.div`

    padding-right: 4rem;
    padding-bottom: 20px; 
    margin: 2rem 0;
    width: 100%;
    text-align: center;
    width: 300px;

    font-size: 1rem;

`
