import React, { useState, useEffect } from 'react';
//scripts to deploy google maps. 
import { GoogleMap, withScriptjs, withGoogleMap, Marker, InfoWindow } from 'react-google-maps';
import {
    requestParks, retrieveParks, retrieveParksError,
    requestHosts, retrieveHosts, retrieveHostsError
} from '../actions/parkActions';

import { useDispatch, useSelector } from 'react-redux';
//components
import ParkSidebar from '../ParkSidebar';


import styled from 'styled-components';



const Map = ({ coordinates }) => {
    //pass down coordinates as props.

    const dispatch = useDispatch();
    const allParks = useSelector(state => state.parkReducer)
    const [parkInfo, setParkInfo] = useState(null)
    //bool to control pakr clicking. 
    const [parkMenu, setParkMenu] = useState(false);


    //get hosted parks.






    const handleParks = () => {
        const handleNearestPlaces = async () => {
            dispatch(requestParks())

            try {
                let response = await fetch('/nearbySearch', {
                    method: "POST",
                    headers: {
                        'Accept': 'application/json',
                        'Content-type': 'application/json'
                    },
                    body: JSON.stringify(coordinates)
                })
                let nearbyParks = await response.json();
                if (response.status === 200) {
                    dispatch(retrieveParks(nearbyParks.results))
                }
                else {
                    dispatch(retrieveParksError())
                }
            }
            catch (err) {
                console.log(err)
            }
            //HOSTS - BETTER WAY TO DO THIS.
            dispatch(requestHosts())

            try {
                let response = await fetch('/getParksWithHosts', {
                    method: "GET",
                    headers: {
                        'Accept': 'application/json',
                        'Content-type': 'application/json'
                    },
                })
                let allHosts = await response.json();
                if (response.status === 200) {
                    console.log(allHosts)
                }

            }
            catch (err) {
                console.log(err)
            }
        }
        handleNearestPlaces();
    }

    //control slide in modal for when selecting a park.
    const handleParkSelect = (park) => {
        setParkInfo(park)
        setParkMenu(true)
    }

    return (
        <>
            <ParkButton onClick={handleParks}>FIND PARKS</ParkButton>
            <Sidebar style={(parkMenu) ? { transform: "translateX(0px)" } : {
                transform: "translateX(-400px)"
            }}>
                {/* park info needs to be loaded.  */}
                {parkInfo !== null && <ParkSidebar parkInfo={parkInfo} parkMenu={parkMenu} setParkMenu={setParkMenu}></ParkSidebar>}
            </Sidebar>
            <GoogleMap defaultZoom={15}
                center={{ lat: coordinates.lat, lng: coordinates.lng }}>

                {/* looping through all parks */}
                {allParks.status === 'retrieved parks.' && allParks.parks.map(park => {
                    return (
                        <Marker
                            style={{ backgroundColor: 'blue' }}
                            position={park.geometry.location} onClick={() => handleParkSelect(park)} />
                    )
                })}
                <Marker position={coordinates}></Marker>
                {parkInfo !== null &&
                    //control the tippy for each map point.
                    <InfoWindow
                        position={{
                            lat: parkInfo.geometry.location.lat,
                            lng: parkInfo.geometry.location.lng
                        }}
                        onCloseClick={() => setParkInfo(null)}>
                        <div>
                            <div>park details</div>
                            <div>{parkInfo.name}</div>
                        </div>
                    </InfoWindow>}
            </GoogleMap>
        </>

    )

}
//must wrap.
const WrappedMap = withScriptjs(withGoogleMap(Map));


export default WrappedMap;

const Sidebar = styled.div`
display: flex;
flex-direction: column;
flex-wrap: wrap;

   position: fixed; 
   margin-top: 30px;
    left: 0; 
    width:400px;
    height: 70vh; 
    transition-duration: .7s;
    top: 27.5vh; 

    background-image: linear-gradient(-60deg, #16a085 0%, #f4d03f 100%);

    `

const ParkButton = styled.button`
    position: absolute;
    top: 27vh;
    right: 47vw;
    border-radius: 25px;
    padding: 5px;
    font-size:1.2rem;
    outline: none;
    transition: ease all 1s;


    &:hover {
        cursor: pointer;
        background-image: linear-gradient(-60deg, #16a085 0%, #f4d03f 100%);


    }

    `
