import React from 'react';
import styled from 'styled-components';
import { PageWrapper } from '../Constants/Constants'
import mainImage from '../../Images/SportHome.jpg'
import { NavLink } from 'react-router-dom';


const Home = () => {

    return (
        <StyledHomeImg>
            <img src='/running.png'></img>
            <h1>FIND ANY SPORT NEARBY</h1>
            <div>
                <NavigationLink exact to='/'><h2>Home</h2></NavigationLink>
                <NavigationLink exact to='/sports'><h2>Sports</h2></NavigationLink>
            </div>



        </StyledHomeImg>



    )

}

export default Home;

const StyledHomeImg = styled.div`
background-image: linear-gradient(15deg, #13547a 0%, #80d0c7 100%);
    height: 45rem; 
    position: relative; 
    background-position: right; 
    color: white;

    img {
  width: 10rem;
  height: 10rem;
  position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -120%);
}

h1 {
    text-align: center;
    position:relative;
    top: 22rem;
    font-size: 4rem;
}
div {
    display: flex;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, 120%);


}

`

const NavigationLink = styled(NavLink)`
    /* text-decoration: none;
    color: black;
    font-weight: 600; 
    transition-duration: 400ms; 
    border-radius: 25px; */
  font-size: 1.1rem;
  background: none;
  border-radius: 25px;

  transition: 0.5s all ease;
  text-decoration: none;
  padding: 10px;
  color: white;


`
