import React, { useState, useEffect } from 'react';
import styled from 'styled-components'


const ParkSidebar = ({ parkInfo, parkMenu, setParkMenu }) => {

    console.log(parkInfo)

    useEffect(() => {

        const handlePhoto = async () => {



            let photo = parkInfo.photos[0].photo_reference;
            try {
                let response = await fetch('/parkPhoto', {
                    method: "POST",
                    headers: {
                        'Accept': 'application/json',
                        'Content-type': 'application/json'
                    },
                    body: JSON.stringify(photo)
                })
            }
            catch (err) {
                console.log(err)
            }

        }
        handlePhoto();
    }, [])



    return (
        <>
            <div>
                {parkInfo !== null &&
                    <Wrapper>
                        <Title>
                            <h1>{parkInfo.name}</h1>
                            <img src={parkInfo.icon}></img>
                        </Title>

                        <h2>{parkInfo.formatted_address}</h2>
                        <div>Ratings: {parkInfo.user_ratings_total}</div>
                    </Wrapper>}
                {parkMenu && <Btn onClick={() => setParkMenu(false)}>X</Btn>}
            </div>



        </>

    )

}


export default ParkSidebar;

const Btn = styled.button`
position: absolute;
top: 30vh;
left: 400px;
z-index: 101;
transition-duration: 5s;

`

const Wrapper = styled.div`


word-wrap: break-word;

padding-right: 4rem;
    padding-bottom: 20px; 
    margin: 2rem 0;
    width: 400px;
    text-align: right;

    font-size: 1rem;

`

const Title = styled.div`
margin-top: 2.5rem;
display: flex;
justify-content: center;
position: relative;

img {
    position: absolute;
    bottom: 40px;
}


`
