const intitialState = {
    status: 'idle',
    isAuthenticated: false,
    isLoading: false,
    token: null,//get token
    user: null,


};

export default function userReducer(state = intitialState, action) {
    switch (action.type) {
        //singup or login success
        case "LOGIN_SUCCESS": {
            console.log(action)
            return {
                ...state,
                status: 'authenticated',
                isAuthenticated: true,
                isLoading: false,
                user: action.user.name,
                token: action.user.token

            }
        }
        case "LOGIN_REQUEST": {
            return {
                ...state,
                status: 'authenticating...',
                isLoading: true,
            }
        }
        case "LOGIN_ERROR": {
            return {
                ...state,
                isLoading: false,
                isAuthenticated: false,
                token: null,
                status: action.payload,
            }
        }
        case "LOGOUT": {
            return {
                ...intitialState
            }
        }

        default:
            return state;
    }
}
