import React, { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
// import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Dialog from '@material-ui/core/Dialog';
import styled from "styled-components";


//
import { receiveUserInfo, requestUserInfo, receiveUserInfoError } from '../actions/userActions';
import { useDispatch, useSelector } from 'react-redux';



//

//Reference Sebastian Silbermann - Materials UI OpenSource Code


function Copyright() {
    return (
        <Typography variant="body2" color="textSecondary" align="center">
            {'Copyright Â©  '}
            <Link color="inherit" href="/">
                Park Sports
      </Link>{' '}
            {new Date().getFullYear()}
            {'.'}
        </Typography>
    );
}

const useStyles = makeStyles((theme) => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main,
    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
}));

export default function SignIn() {

    const [open, setOpen] = useState(false);
    const [error, setError] = useState(false)

    //
    const dispatch = useDispatch();

    //user info and password. 
    const [userInfo, setUserInfo] = useState({
        user: '',
        pass: '',
    })


    const classes = useStyles();


    const handleClickOpen = () => {
        setOpen(true);
        setError(false)
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleDone = (e) => {
        e.preventDefault();
        //action to request user data.
        dispatch(requestUserInfo())

        const handleLogin = async () => {
            let response = await fetch('/Login', {
                method: "POST",
                headers: {
                    'Accept': 'application/json',
                    'Content-type': 'application/json'
                },
                body: JSON.stringify(userInfo)
            })

            if (response.status === 200) {
                let userResponse = await response.json();
                let name = userResponse.username.split('@')[0]
                console.log(userResponse)
                //dispatch to make userState
                dispatch(receiveUserInfo(name))
                setOpen(false)
            }
            else {
                dispatch(receiveUserInfoError())
            }

        }
        //function call.
        handleLogin();


    }




    return (<>
        <StyledLoginButton onClick={handleClickOpen}>
            Login
</StyledLoginButton>

        <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
            <Container component="main" maxWidth="xs">
                <CssBaseline />
                <div className={classes.paper}>
                    <Avatar className={classes.avatar}>
                        {/* <LockOutlinedIcon /> */}
                    </Avatar>
                    <Typography component="h1" variant="h5">
                        Login
        </Typography>
                    <form className={classes.form} onSubmit={handleDone}>
                        <TextField
                            required
                            variant="outlined"
                            margin="normal"

                            fullWidth
                            id="email"
                            label="Email Address"
                            name="email"
                            autoComplete="email"
                            autoFocus
                            onChange={(e) => setUserInfo({
                                ...userInfo,
                                user: e.target.value,
                            })}
                            value={userInfo.user}
                            helperText={!error ? '' : "User not found. You may need to Sign Up!"}
                        />
                        <TextField
                            required
                            variant="outlined"
                            margin="normal"
                            fullWidth
                            name="password"
                            label="Password"
                            type="password"
                            id="password"
                            autoComplete="current-password"
                            onChange={(e) => setUserInfo({
                                ...userInfo,
                                pass: e.target.value,
                            })}
                            value={userInfo.pass}

                        />
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            color="primary"
                            className={classes.submit}
                        >
                            Sign In
                     </Button>
                        <Button onClick={handleClose}

                            fullWidth
                            variant="contained"
                            color="inherit"
                            className={classes.submit}
                        >
                            Cancel
                        </Button>
                        <Grid container>
                            <Grid item>
                                <Button>
                                    Don't have an account?
                                </Button>
                            </Grid>
                        </Grid>
                    </form>
                </div>
                <Box mt={8}>
                    <Copyright />
                </Box>
            </Container>


        </Dialog>

    </>);
}

const StyledLoginButton = styled.div`
 text-align: center;
  padding: 0 20px 0 20px;
  font-size: 1.1rem;
  background: none;
  border-radius: 25px;
  transition: 0.5s all ease;

  &:hover {
      cursor: pointer;
      background-image: linear-gradient(-60deg, #16a085 0%, #f4d03f 100%);

  }

`
