const intitialState = {
    status: 'idle',
    isLoading: false,
    token: localStorage.getItem('token'),//get token
    user: null,


};

export default function userReducer(state = intitialState, action) {
    switch (action.type) {
        case "RECEIVE_USER_DATA": {
            return {
                ...state,
                status: 'authenticated',
                user: action.user,
                isLoading: false,


            }
        }
        case "REQUEST_USER_DATA": {
            return {
                ...state,
                status: 'authenticating...',
                isLoading: true,


            }
        }
        case "RECEIVE_USER_ERROR": {
            return {
                ...state,
                status: 'error authenticating',
            }
        }
        case "LOGOUT": {
            return {
                ...intitialState
            }
        }

        default:
            return state;
    }
}
