import React, { useState } from 'react';
import PlacesAutocomplete, {
    geoCodeByAddress,
} from 'react-places-autocomplete';
import Geocode from "react-geocode";
import styled from 'styled-components';



const GoogleAddress = ({ setCoordinates }) => {

    const [address, setAddress] = useState('')


    const handleSelect = async (value) => {

        Geocode.setApiKey("AIzaSyAt-D4AMalUpyQjUe3laQYyjjNgy_hcCOc");
        // set response language. Defaults to english.
        Geocode.setLanguage("en");
        // set response region. Its optional.
        // A Geocoding request with region=es (Spain) will return the Spanish city.
        Geocode.setRegion("ca");
        // Enable or disable logs. Its optional.
        Geocode.enableDebug();
        // Get latidude & longitude from address.
        Geocode.fromAddress(value).then(
            response => {
                const { lat, lng } = response.results[0].geometry.location;
                console.log(lat, lng);
                setCoordinates({
                    lat: lat,
                    lng: lng
                })
            },
            error => {
                console.error(error);
            }
        );
    }

    return (
        <div>
            <PlacesAutocomplete value={address} onChange={setAddress} onSelect={handleSelect}>
                {/*wants render prop function as child.  */}
                {({ getInputProps, suggestions, getSuggestionItemProps, loading }) =>
                    <div><StyledInput {...getInputProps({ placeholder: "type address" })}></StyledInput>
                        <div>
                            {loading ? <div>...loading</div> : null}
                            {/* add styling. */}
                            {suggestions.map((suggestion) => {
                                return <div {...getSuggestionItemProps(suggestion)}> {suggestion.description}</div>
                            })}
                        </div>
                    </div>}
            </PlacesAutocomplete>
        </div>
    )


}

export default GoogleAddress;

const StyledInput = styled.input`
`
