import React, { useState, useEffect } from "react";
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import Login from '../Login';
import Signup from '../Signup';
import { useDispatch, useSelector } from 'react-redux';



const Navbar = () => {


  const [loginState, setLoginState] = useState(true)

  //user redux state.
  const userLoggedIn = useSelector(state => state.userReducer)








  return <>
    <Header>
      <h1>Park Games</h1>
      <nav>

        <NavigationLink exact to='/'><h2>Home</h2></NavigationLink>
        {userLoggedIn.status !== "authenticated" && <Login setLoginState={setLoginState}></Login>}
        {userLoggedIn.status !== "authenticated" && <Signup setLoginState={setLoginState}></Signup>}
        {/* Show User Name */}
        {userLoggedIn.status === "authenticated" && <StyledName>{userLoggedIn.user.toUpperCase().split('')[0]}</StyledName>}


      </nav>


    </Header>

  </>

}

export default Navbar;

const Header = styled.header`
background: white;

h1 {
  text-align: center;
}
h2 {
  text-align: center;
  padding: 0 20px 0 20px;
  font-size: 1.1rem;


}
nav{
  display: flex;
  justify-content: center;
}
`

const NavigationLink = styled(NavLink)`
    text-decoration: none;
    color: black;
    font-weight: 600; 
    transition-duration: 400ms; 
`


const StyledName = styled.div`
background-color: green;
border-radius: 50%;
position: absolute;
top: 0%;
right: 0%;
padding: 5px;
`
