import React, { useState } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import WrappedMap from '../Map/Map';
import { PageContainer } from '../Constants/Constants'


//
import GoogleAddress from '../GoogleAddress';




const Sports = () => {

    const userLoggedIn = useSelector(state => state.userReducer)
    const [coordinates, setCoordinates] = useState({
        lat: null,
        lng: null
    })

    const [parkMenu, setParkMenu] = useState(false);



    //sliding info modal.

    return (
        <Wrapper>
            <button onClick={() => setParkMenu(!parkMenu)}>OPEN</button>
            <Sidebar style={(parkMenu) ? { transform: "translateX(0px)" } : {
                transform: "translateX(-400px)"
            }}>
                <Wrapper2>
                    <div>info</div>
                    <h1>KJHSIFHSKDHFKJSDHFKJSDHFKJSHFKJSDFKJHSD</h1>
                    <h2>KJHSIFHSKDHFKJSDHFKJSDHFKJSHFKJSDFKJHSD</h2>


                </Wrapper2>
            </Sidebar>

            {parkMenu && <Btn onClick={() => setParkMenu(false)}>X</Btn>}

            {userLoggedIn.status !== "authenticated" ?
                <h1>You must be logged in to find games.</h1> :
                <div>
                    {/* // */}
                    <div style={{ padding: '10px' }}>
                        <h1>Welcome. Start finding people at your local park!</h1>
                        <GoogleAddress setCoordinates={setCoordinates}></GoogleAddress>
                    </div>

                    {/* //call component to find games. */}
                    <StyledMap style={{ width: '100vw', height: '70vh' }}>
                        {coordinates.lat !== null && <WrappedMap
                            coordinates={coordinates}
                            googleMapURL={`https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyAt-D4AMalUpyQjUe3laQYyjjNgy_hcCOc`}
                            loadingElement={<div style={{ height: '100%' }}></div>}
                            containerElement={<div style={{ height: '100%' }}></div>}
                            mapElement={<div style={{ height: '100%' }}></div>}
                        ></WrappedMap>}
                    </StyledMap>
                </div>

            }
        </Wrapper>

    )

}

export default Sports;

const Wrapper = styled.div`

h1 {
    text-align: center;
}

`

const StyledMap = styled.div`
`



///
const Sidebar = styled.div`
   position: fixed; 
    left: 0; 
    width:400px;
    height: 70vh; 
    transition-duration: .7s;
    top: 30vh; 

    background-image: linear-gradient(-60deg, #16a085 0%, #f4d03f 100%);

    `
const Btn = styled.button`
position: absolute;
top: 30vh;
left: 400px;
z-index: 101;
transition-duration: 5s;


`

const Wrapper2 = styled.div`

padding-right: 4rem;
    padding-bottom: 20px; 
    margin: 2rem 0;
    width: 100%;
    text-align: right;
    width: 300px;

    font-size: 1rem;



`
