import React, { useState, useEffect } from 'react';
import styled from 'styled-components'
import { AiOutlineClose } from 'react-icons/ai';
import { Link, useHistory } from 'react-router-dom';


import { useDispatch, useSelector } from 'react-redux';
import { selectPark } from '../actions/parkActions';






const ParkSidebar = ({ parkInfo, parkMenu, setParkMenu }) => {

    console.log(parkInfo)
    let history = useHistory();
    const dispatch = useDispatch();



    const [image, setImage] = useState(null)
    const userLoggedIn = useSelector(state => state.userReducer)
    const hostsInfo = useSelector(state => state.hostReducer)

    if (hostsInfo.hosts !== null) {
        console.log(hostsInfo.hosts)


    }




    //-----------------------SELECTED PARK PHOTO ------------------------------
    useEffect(() => {
        const handlePhoto = async () => {
            if (parkInfo.photos === undefined) {
                //put something that does exist. 
                console.log('photo does not exist')
            }
            else {

                let photo = parkInfo.photos[0].photo_reference;
                console.log(photo, 'inside handle photo')
                try {
                    let response = await fetch('/parkPhoto', {
                        method: "POST",
                        headers: {
                            'Accept': 'application/json',
                            'Content-type': 'application/json'
                        },
                        body: JSON.stringify({ photo })
                    })
                    if (response.status === 200) {
                        let image = await response.json();
                        setImage(image.image)
                    }
                    else {
                        console.log('error occured fetching park image.')
                    }
                }
                catch (err) {
                    console.log("error occured in Catch for handlePhoto", err)
                }
            }


        }
        handlePhoto();
    }, [parkInfo])

    const handleHostView = () => {
        dispatch(selectPark(parkInfo))
        history.push('/hostPark')
    }





    return (
        <>

            {parkInfo !== null &&
                <Wrapper>

                    <Title>
                        <h1>{parkInfo.name}</h1>
                        <img src={parkInfo.icon}></img>
                    </Title>

                    <h2>{parkInfo.formatted_address}</h2>
                    {image !== null && <img src={image}></img>}
                    <div>Ratings: {parkInfo.user_ratings_total}</div>
                    <div>



                        <button onClick={handleHostView}>Host Activity</button>


                        <Link to='/viewActivity'>View Activity</Link>

                    </div>
                    {parkMenu && <Btn onClick={() => setParkMenu(false)}><AiOutlineClose></AiOutlineClose></Btn>}

                </Wrapper>}





        </>

    )

}


export default ParkSidebar;

const Btn = styled.div`
position: absolute;
top: 0vh;
left: 375px;
z-index: 101;
transition-duration: 5s;
border: none;
outline: none;
margin-right: 10px;
margin-top: 10px;
&:hover {
    cursor: pointer;
}

`

const Wrapper = styled.div`

word-wrap: break-word;
padding-right: 4rem;
    padding-bottom: 20px; 
    margin: 2rem 0;
    width: 400px;
    text-align: center;
    font-size: 1rem;

    h2 {
        font-size: 1.1rem;
        padding: 1rem;
    }
`

const Title = styled.div`
margin-top: 2.5rem;
display: flex;
justify-content: center;
position: relative;

img {
    position: absolute;
    bottom: 50px;
}


`
