import React, { useState } from 'react';
import styled from 'styled-components'


const ParkSidebar = ({ parkInfo, parkMenu, setParkMenu }) => {



    return (
        <>{parkInfo &&
            <Sidebar style={(parkMenu) ? { transform: "translateX(-70%)" } : {
                transform: "translateX(-100%)"
            }}>
                <Wrapper>
                    <div>{parkInfo.photos[0].html_attributions[0]}</div>
                    <h1>{parkInfo.name}</h1>
                    <h2>{parkInfo.formatted_address}</h2>
                    <img src={parkInfo.icon}></img>



                </Wrapper>



                {parkMenu && <Btn onClick={() => setParkMenu(false)}>X</Btn>}

            </Sidebar>
        }

        </>

    )

}


export default ParkSidebar;

const Sidebar = styled.div`
    position: fixed; 
    right:0; 
    width:100%;
    height: 70vh; 
    transition-duration: .7s;
    top: 25vh; 
    z-index: 100; 
    background-image: linear-gradient(60deg, #64b3f4 0%, #c2e59c 100%);`

const Btn = styled.button`
position: absolute;
top: 25vh;
right: 70vw;
z-index: 101;
transition-duration: 5s;

`

const Wrapper = styled.div`
z-index: 102;


`
