import React, { useState, useEffect } from "react";
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import Login from '../Login';
import Signup from '../Signup';
import { useDispatch, useSelector } from 'react-redux';
import { logOutUser } from '../actions/userActions';

//





const Navbar = () => {



  //user redux state.
  const userLoggedIn = useSelector(state => state.userReducer)
  //dispatch
  const dispatch = useDispatch();









  return <>
    <Header>
      <h1>Park Games</h1>
      {userLoggedIn.status !== "authenticated" ? <Login></Login> : <StyledLoginButton onClick={() => dispatch(logOutUser())}>Logout</StyledLoginButton>}
      {userLoggedIn.status !== "authenticated" && <Signup></Signup>}
      <nav>

        <NavigationLink exact to='/'><h2>Home</h2></NavigationLink>
        <NavigationLink exact to='/sports'><h2>Sports</h2></NavigationLink>

        {/* Show User Name */}
        {userLoggedIn.status === "authenticated" && <StyledName>{userLoggedIn.user.toUpperCase().split('')[0]}</StyledName>}



      </nav>


    </Header>

  </>

}

export default Navbar;

const Header = styled.header`
background: white;

h1 {
  text-align: center;
  font-size: 3rem;
  padding: 10px;
}
h2 {
  text-align: center;
  padding: 0 20px 0 20px;
  font-size: 1.1rem;


}
nav{
  display: flex;
  justify-content: center;
  padding: 20px;
}

`

const NavigationLink = styled(NavLink)`
    text-decoration: none;
    color: black;
    font-weight: 600; 
    transition-duration: 400ms; 
    border-radius: 25px;


    &:hover {
      background-image: linear-gradient(-60deg, #16a085 0%, #f4d03f 100%);
    }
`


const StyledName = styled.div`
      background-image: linear-gradient(-60deg, #16a085 0%, #f4d03f 100%);
border-radius: 50%;
position: absolute;
top: 0%;
left: 0.5%;
padding: 10px 20px 10px 20px;
font-size: 1.5rem;
`

const StyledLoginButton = styled.div`
 text-align: center;
  padding: 0 20px 0 20px;
  font-size: 1.1rem;
  background: none;
  border-radius: 25px;
  transition: 0.5s all ease;

  &:hover {
      cursor: pointer;
      background-image: linear-gradient(-60deg, #16a085 0%, #f4d03f 100%);

  }

`
