import React, { useState } from 'react'
import { PageWrapper } from '../Constants/Constants'
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';



const parkInfo =
{
    business_status: 'OPERATIONAL',
    formatted_address: 'Rue Fleury O, Montréal, QC H3L 1B9, Canada',
    geometry: {
        location: {
            lat: 45.5440696,
            lng: -73.6655919
        },
        viewport: {
            northeast: {
                lat: 45.54563832989272,
                lng: -73.66410922010726
            },
            southwest: {
                lat: 45.54293867010728,
                lng: -73.66680887989271
            }
        }
    },
    icon: 'https://maps.gstatic.com/mapfiles/place_api/icons/generic_recreational-71.png',
    id: '0954f1fa71a785bbbdc1fce14df48647a9ea5d57',
    name: 'Parc Tolhurst',
    opening_hours: {
        open_now: true
    },
    photos: [
        {
            height: 3456,
            html_attributions: [
                '<a href="https://maps.google.com/maps/contrib/101021804328762220817">paul rossini</a>'
            ],
            photo_reference: 'CmRaAAAAdbfA3pze3_YBS_dpMcBOEopwoHWrSodO-2-fP9-FYJ7KCBcpKhgu7U3T90mz_8W6rFxrFKy0VI9N31LcxlAuN4UakpvJ4pAunHcd_24hpWwlRt2NAqQxfyYRIBpr7w8HEhAi9cdyOZB7i89zi3sfC6BmGhTDUSvzr4pYleLWaKaLxXi8SHz_oA',
            width: 4608
        }
    ],
    place_id: 'ChIJwUGDhpMYyUwRMCTSeBh8Z8E',
    plus_code: {
        compound_code: 'G8VM+JQ Montreal, Quebec',
        global_code: '87Q8G8VM+JQ'
    },
    rating: 4.3,
    reference: 'ChIJwUGDhpMYyUwRMCTSeBh8Z8E',
    types: [
        'park',
        'point_of_interest',
        'establishment'
    ],
    user_ratings_total: 143
}




const Host = () => {

    const userLoggedIn = useSelector(state => state.userReducer)
    //use this for real.
    const selectedPark = useSelector(state => state.parkReducer.selectedPark)

    //

    const [sportSelect, setSportSelect] = useState(null)
    const [skillSelect, setSkillSelect] = useState(null)
    const [time, setTime] = useState(null)





    const sports = [
        "Choose sport",
        "Soccer",
        "Tennis",
        "Basket-Ball",
        "Badminton",
        "Hockey",
        "Dodgeball",
        "Volleyball",
    ]

    const skillLevel = [
        "Choose skill level",
        "Beginner",
        "Intermediate",
        "Advanced"

    ]
    const times = [
        "Morning",
        "Afternoon",
        "Evening",
        "Night"
    ]


    const handleHostInformation = async (event) => {
        event.preventDefault();

        let hostingInformation = {
            token: userLoggedIn.token,
            name: userLoggedIn.user,
            sport: sportSelect,
            skill: skillSelect,
            time: time,
            //replace witht he actual park. 
            parkId: '0954f1fa71a785bbbdc1fce14df48647a9ea5d57',
        }

        let response = await fetch("/hostingInformation", {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                Accept: 'application/json',
                'Authorization': `${hostingInformation.token}`
            },
            body: JSON.stringify(hostingInformation)

        })


    }






    return (

        <PageWrapper>

            <form onSubmit={handleHostInformation}>
                <select onChange={(event) => setSportSelect(event.target.value)}>
                    {sports.map(sport => {
                        return (
                            <option>{sport}</option>
                        )
                    })}
                </select>
                <select onChange={(event) => setSkillSelect(event.target.value)}>
                    {skillLevel.map(skill => {
                        return (
                            <option>{skill}</option>
                        )
                    })}
                </select>
                <select onChange={(event) => setTime(event.target.value)}>
                    {times.map(time => {
                        return (
                            <option>{time}</option>
                        )
                    })}
                </select>
                {/* <input type='text'></input>
                <input type='text'></input> */}
                <button type='submit'>Submit</button>
            </form>
        </PageWrapper>
    )

}

export default Host;
