import React, { useState } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import WrappedMap from '../Map/Map';

//
import Geocode from "react-geocode";




const Sports = () => {

    const userLoggedIn = useSelector(state => state.userReducer)

    const [mapBool, setMapBool] = useState(false);
    const [address, setAddress] = useState('')


    //handlers.

    const handleMapBool = (e) => {

        e.preventDefault();
        Geocode.setApiKey("AIzaSyAt-D4AMalUpyQjUe3laQYyjjNgy_hcCOc");
        // set response language. Defaults to english.
        Geocode.setLanguage("en");
        // set response region. Its optional.
        // A Geocoding request with region=es (Spain) will return the Spanish city.
        Geocode.setRegion("ca");
        // Enable or disable logs. Its optional.
        Geocode.enableDebug();
        // Get latidude & longitude from address.
        Geocode.fromAddress('9165 Clark St Montreal, QC H2N 2K4').then(
            response => {
                const { lat, lng } = response.results[0].geometry.location;
                console.log(lat, lng);
                setMapBool(true)
            },
            error => {
                console.error(error);
            }
        );
    }



    return (
        <Wrapper>
            {userLoggedIn.status !== "authenticated" ?
                <h1>You must be logged in to find games.</h1> :


                <div>

                    {/* // */}
                    <h1>Welcome. Enter an address to begin.</h1>
                    {/* //call component to find games. */}
                    <form onSubmit={handleMapBool}>
                        <input type='text' placeholder='enter address' onChange={(e) => setAddress(e.target.value)}></input>
                        <button type='submit'>Go</button>
                    </form>

                    {mapBool && <div style={{ width: '100vw', height: '80vh' }}>
                        <WrappedMap
                            // googleMapURL={`https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyAt-D4AMalUpyQjUe3laQYyjjNgy_hcCOc`}
                            loadingElement={<div style={{ height: '100%' }}></div>}
                            containerElement={<div style={{ height: '100%' }}></div>}
                            mapElement={<div style={{ height: '100%' }}></div>}
                        ></WrappedMap>
                    </div>}
                </div>

            }
        </Wrapper>

    )

}

export default Sports;

const Wrapper = styled.div`

h1 {
    text-align: center;
}

`
