import React from 'react';


const ParkSidebar = () => {
    const [parkMenu, setParkMenu] = useState(false);



    return (
        <>
            <button onClick={() => setParkMenu(!parkMenu)}>slide</button>
            <Sidebar style={(parkMenu) ? { transform: "translateX(-70%)" } : {
                transform: "translateX(-100%)"
            }}></Sidebar>
            {parkMenu && <Btn onClick={() => setParkMenu(false)}>X</Btn>}

        </>

    )

}


export default ParkSidebar;

const Sidebar = styled.div`
    position: fixed; 
    position: relative;
    right:0; 
    width:100%;
    height: 70vh; 
    transition-duration: .7s;
    top: 0; 
    z-index: 100; 
    background-image: linear-gradient(to top, #c1dfc4 0%, #deecdd 100%);    
`

const Btn = styled.button`
position: absolute;
top: 0vh;
right: 70vw;
z-index: 101;
transition-duration: 5s;

`
